document.addEventListener("DOMContentLoaded", function () {
    const selectedRoast = localStorage.getItem("selectedRoast");

    if (selectedRoast) {
        const selectedElement = document.getElementById(selectedRoast);
        
        if (selectedElement) {
            selectedElement.classList.add("selected");
            const imageContainer = selectedElement.querySelector(".image-container");

            if (imageContainer) {
                imageContainer.style.display = "block";
                imageContainer.innerHTML = '<img src="data:image/jpeg;base64," alt="Coffee Beans">';
            }
        }
    }
});

function selectRoast(type) {
    localStorage.setItem("selectedRoast", type);

    document.querySelectorAll(".roast").forEach(roast => {
        roast.classList.remove("selected");
        const imageContainer = roast.querySelector(".image-container");
        
        if (imageContainer) {
            imageContainer.style.display = "none";
        }
    });

    const selectedElement = document.getElementById(type);

    if (selectedElement) {
        selectedElement.classList.add("selected");
        const imageContainer = selectedElement.querySelector(".image-container");

        if (imageContainer) {
            imageContainer.style.display = "block";
            imageContainer.innerHTML = '<img src="data:image/jpeg;base64," alt="Coffee Beans">';
        }
    }
}